	function Cr(tx){
		var tex=tx;//document.getElementById('email').value;
		var largo=tex.length;
		var c=0;
		var p=0;
		for (var i = 1; i < largo; i++) {
			if(tex.charAt(i-1)=="@"){
				c++;
			}
			if(c==1){
			if(tex.charAt(i-1)=="."){
				p++;	
			}
			}
		}
		if(c==1 && p==2 || p==1){
			//alert("Correo Valido");
			////document.getElementById('email').className="verde";
			return tx;
		}
		else
		{
			//alert("Correo no valido");
			////document.getElementById('email').className="rojo";
			return "nd";
		}
	}
	function cmTexto(e){
		key=e.keyCode || e.which;
		key2=String.fromCharCode(key).toLowerCase();
		abc=" abcdefghijklmnñopqrstuvwxyz.,-_áéíóú";
		if(key2===32)
		{
			//alertify.error("El campo esta vacio");
			return false;
		}
		else if(key2!=32)
		{
			if(abc.indexOf(key2)==-1 )
			{
				alertify.error("Debe ingresar solo letras");
				return false;
			}
		}
	}
	function cmTextoNum(e){
		key=e.keyCode || e.which;
		key2=String.fromCharCode(key).toLowerCase();
		abc=" abcdefghijklmnñopqrstuvwxyz.,-_áéíóú0123456789\n\r";
		if(key2===32)
		{
			//alertify.error("El campo esta vacio");
			return false;
		}
		else if(key2!=32)
		{
			if(abc.indexOf(key2)==-1)
			{
				alertify.error("Debe ingresar solo letras y numeros");
				return false;
			}
		}
	}
