	function actpc(){
		$('#btnpacienteact').attr('disabled','true');
		$('#content').html('<div class="content"></div>');
		id=$('#nid').val();

		td=$('#tdoc').val();
		nd=$('#ndoc').val();
		n=$('#pnombre').val();
		n2=$('#snombre').val();
		ap=$('#pape').val();
		ap2=$('#sape').val();
		ln=$('#lugarnaci').val();
		fn=$('#fnaci').val();
		tv=$('#tpv').val();
		ta=$('#tpa').val();
		asegura=$('#tpasegura').val();
		ambito=$('#aregistro').val();
		direccion=$('#direc').val();
		barrio=$('#barri').val();
		municipio=$('#muni').val();
		telfijo=$('#telfijo').val();
		telcel=$('#telcel').val();
		ocuapacion=$('#ocupa').val();
		religion=".";//$('#reli').val();
		ecivil=$('#tpec').val();
		ncuidador=$('#ncuidador').val();
		telcuidador=$('#telcuidador').val();
		nresponsable=$('#nresponsable').val();
		tresponsable=$('#telresponsable').val();
		parece=$('#parece').val();
		minclusion=$('#minclusion').val();
		lv=$('#lugarv').val();
		// fv=$('#fvincula').val();
		// hv=$('#hvincula').val();
		mpad=$('#motivopad').val();

		cadena="idpc="+id+"&tdoc="+td+"&ndoc="+nd+"&pnombre="+n+"&snombre="+n2+
				"&papellido="+ap+"&sapellido="+ap2+"&lugarn="+ln+"&fnaci="+fn+
				"&tipov="+tv+"&tipoa="+ta+"&asegura="+asegura+"&ambito="+ambito+
				"&direccion="+direccion+"&barrio="+barrio+"&municipio="+municipio+"&telfijo="+telfijo+
				"&telcel="+telcel+"&ocupacion="+ocuapacion+"&religion="+religion+"&estadoc="+ecivil+
				"&ncuidador="+ncuidador+"&telcuidador="+telcuidador+"&nresponsable="+nresponsable+"&tresponsable="+tresponsable+
				"&parece="+parece+"&minclusion="+minclusion+"&lugarv="+lv+"&mpad="+mpad;

		// $('#confirmar').modal('hide');
		// console.log(cadena);
		$.ajax({
			type:"POST",
			url:estaeslabaseurl+"paciente/actualizar",
			data:cadena,
			success:function(respu){
				$('#content').fadeIn(2000).html('');
				$('#confirmar').modal('hide');
				$('#btnpacienteact').removeAttr('disabled');
				if(respu==1)
				{
					alertify.alert('Nombre de la entidad','<span class="alert-success" role="alert"><strong>¡Cambios Realizados!</strong></span>', 
					function(){ }).set('closable',false);
				}
				else
				{
					alertify.error("No se logro actualizar");
				}
			}
		});
		return false;
	}
