		var misopcionestdoc=document.getElementById('tdoc');
		var opcionesmotivo=document.getElementById('ttrata');

		while(misopcionestdoc.options.length){
			misopcionestdoc.remove(0);
		}
		while(opcionesmotivo.options.length){
			opcionesmotivo.remove(0);
		}
		
		$('#nhc').val(nd);
		$('#ndoc').val(nd);
		$('#elnombre').val(n+' '+ap);
		var r2="";
		ver=dg.split(';');
        for (var j = 0;j<ver.length-1 ;j++)
        {
        	r2+=ver[j]+";"+"\r\n";
        }
		$('#losdg').val(r2);

