	function nrj(){
		var seconds = 119; //número de segundos a contar
		function secondPassed() {

		  var minutes = Math.trunc(seconds/60); //calcula el número de minutos
		  var remainingSeconds = seconds % 60; //calcula los segundos restantes
		  //si los segundos usan sólo un dígito, añadimos un cero a la izq
		  if (remainingSeconds < 10) { 
		    remainingSeconds = "0" + remainingSeconds; 
		  } 
		  document.getElementById('conta').innerHTML = minutes + ":" +     remainingSeconds; 
		  if (seconds == 0) { 
		  	document.getElementById('conta').innerHTML = "00:00"; 
		    clearInterval(countdownTimer); 
		    //alert('Se acabó el tiempo'); 
		    alertify.success('Se acabo el tiempo');
		    $('#btnotravez').removeAttr('disabled');
		    // $('#btnotravez').attr('enabled','');
		  } else { 
		    seconds--; 
		  } 
		} 

		var countdownTimer = setInterval(secondPassed, 1000);
	}
