	function sumartensionarterial(){
		var sistole=$('#sis').val();
		var diastole=$('#dias').val();
		if(sistole=="")
		{
			sistole=0;
		}
		if(diastole=="")
		{
			diastole=0;
		}
		ta=parseInt(diastole) + (parseInt(sistole) - parseInt(diastole) ) /3;
		$('#ta').val(ta.toFixed(2));
	}
